/* eslint-disable react/prop-types, react/jsx-handler-names */

import React from 'react';
import axios from 'axios';
import PropTypes from 'prop-types';
import Select from 'react-select';
import { withStyles } from '@material-ui/core/styles';
import { withRouter} from 'react-router-dom';
import Typography from '@material-ui/core/Typography';
import NoSsr from '@material-ui/core/NoSsr';
import TextField from '@material-ui/core/TextField';
import Paper from '@material-ui/core/Paper';
import MenuItem from '@material-ui/core/MenuItem';

import Config from '../Config';


function NoOptionsMessage(props) {
  return (
    <Typography
      color="textSecondary"
      className={props.selectProps.classes.noOptionsMessage}
      {...props.innerProps}
    >
      {props.children}
    </Typography>
  );
}

function inputComponent({ inputRef, ...props }) {
  return <div ref={inputRef} {...props} />;
}

function Control(props) {
  return (

    <TextField
      fullWidth
      InputProps={{
        inputComponent,
        inputProps: {
          className: props.selectProps.classes.input,
          inputRef: props.innerRef,
          children: props.children,
          ...props.innerProps,
        },
      }}
      {...props.selectProps.textFieldProps}
    />
  );
}

function Option(props) {
  return (
    <MenuItem
      buttonRef={props.innerRef}
      selected={props.isFocused}
      component="div"
      style={{
        fontWeight: props.isSelected ? 500 : 400,
      }}
      {...props.innerProps}
    >    
      {props.children}
    </MenuItem>
  );
}

function Placeholder(props) {
  return (
    <Typography
      color="textSecondary"
      className={props.selectProps.classes.placeholder}
      {...props.innerProps}
    >
      {props.children}
    </Typography>
  );
}

function SingleValue(props) {
  return (
    <Typography className={props.selectProps.classes.singleValue} {...props.innerProps}>
      {props.children}
    </Typography>
  );
}

function ValueContainer(props) {
  return <div className={props.selectProps.classes.valueContainer}>{props.children}</div>;
}

function Menu(props) {
  return (
    <Paper square className={props.selectProps.classes.paper} {...props.innerProps}>
      {props.children}
    </Paper>
  );
}

const components = {
  Control,
  Menu,
  NoOptionsMessage,
  Option,
  Placeholder,
  SingleValue,
  ValueContainer,
};

class Search extends React.Component {
  state = {
    single: "",
    suggestions : [],
  };

// life-cycle methods
componentDidMount(){

  // creating the url
  let proteinURL = Config.settings.apiURL + Config.settings.proteinsEndpoint ;


  // api call to retrieve all data
  axios.get(proteinURL)
      .then(res =>{

          // extract unique sample names
          const unique = [...new Set(res.data.proteins.map(protein => {
            return protein.proteinName
            } ))];

          // creating the suggestions on a sorted list
          var suggestions = unique.sort().map(suggestion => ({
            category: "proteinName",
            value: suggestion,
            label: suggestion,
          }));

            this.setState({
              suggestions: suggestions
            });

          });


      // Setting the title of the browser tab
      document.title = "YEP Topic Project"
}

  handleChange = name => value => {
    this.setState({
        [name]: value,
      });

    //Do nothing when the value is null
      if(!value){
        console.log(value);
      }
      else{
          console.log(this.props)
          //window.location.assign(Config.settings.appURL + '/protein/' + value.label);
          this.props.history.push('/protein/' + value.label);

      }

  };



  render() {
    const { classes, theme } = this.props;
    const {suggestions} = this.state;

    const selectStyles = {
      input: base => ({
        ...base,
        color: theme.palette.text.primary,
        '& input': {
          font: 'inherit',
        },
        padding: 4
      }),
    };

    return (
      <div className={classes.root}>
        <NoSsr>        
        <Paper className={classes.inputContainer} square={true} elevation={1}> 
          <Select
            classes={classes}
            styles={selectStyles}
            options={suggestions}
            components={components}
            value={this.state.single}
            onChange={this.handleChange('single')}
            placeholder="Search your factor"
            isClearable
            autoFocus
          />
           </Paper>
        </NoSsr>
      </div>
    );
  }
}

Search.propTypes = {
  classes: PropTypes.object.isRequired,
  theme: PropTypes.object.isRequired,
};

export default withRouter(withStyles(styles, { withTheme: true })(Search));
//export default withRouter(Search);
