        background: '#f5f5f5' 
      },
      appBar2: {  
        background: '#fff' 
      },
      drawerHeader:{
        display: 'flex',
        alignItems: 'center',
        padding: '0 8px',
        ...theme.mixins.toolbar,
        justifyContent: 'flex-end',    
        height: 75,
      },
      progress: {
        width: 500
      },
      textField: {
        width: 200,
        margin: "auto"
      },
})*/

const styles = theme => ({
  center : {
      margin: 'auto',
      maxWidth: 1140,
      // padding: 10,    
    },
});

class AppContainer extends React.Component {
  state = {        
    data : [],
    loading : true,
    message: "Fetching Samples"
}

componentDidMount(){
  var dataURL = Config.settings.apiURL+Config.settings.topicsEndpoint;
  axios.get(dataURL)
  .then(res =>{
      console.log(res.data.topics);
      //retrieve the protein data
      let values = res.data.topics.map(topic=>{
          return [topic.topicName, (topic.proteinContain).length]
      });
      this.setState({
          data : values,
          loading: false
      });
  }).catch(error =>{
      console.log(error);
      this.setState({
          loading: true,
          message: error.message + "/ Server Offline"
      })
  });
}

render(){
    const {classes} = this.props;
    const {data, loading, message} = this.state;

    const table = loading ? (
      <Typography component="div" className={classes.center}>                   
                <Typography component="p" variant="subtitle1" >
                    {message}
                </Typography>
                <LinearProgress variant="query" />
      </Typography> 
    ) : (
      <SearchTable data={data}/>
  );
  return(
    <div className={classes.center}>
            {table}
            </div>
  )
}
}

