import React, {Component} from 'react';

// material-ui imports
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import Divider from '@material-ui/core/Divider';
import Grid from '@material-ui/core/Grid';
import LaunchIcon from '@material-ui/icons/Launch';
import TrackHubIcon from '@material-ui/icons/AssessmentOutlined';
import InfoIcon from '@material-ui/icons/InfoOutlined';
import Drawer from '@material-ui/core/Drawer';
import DownloadIcon from '@material-ui/icons/GetApp';
import LinearProgress from '@material-ui/core/LinearProgress';

// For Sample Meta information Drawer
import SampleStats from './SampleStats';
import ReplicateTabs from './ReplicateTabs';

// For SampleStats Notifications
import { SnackbarProvider } from 'notistack';
import Tooltip from '@material-ui/core/Tooltip';

// retrieve app configuration settings
import Config from '../Config';

const styles = theme => ({

    center : {
        margin: 'auto',      
        maxWidth: 1200,
        padding: 10,
        // border: '2px solid green'
      },
    card: {
        maxWidth: 1200
    },           
      leftIcon: {
        marginRight: theme.spacing.unit,
        // fontSize: 25,
      },  
      navbar:{

      },
      searchbar:{
        width: 1100
      } 


});

class Sample extends Component{
    state ={
        post : null, // setting it to one sample 
        proteinName :  null,
        drawer :false,
        samples : null,
        loading: true,
        message: "Fetching Samples"
    }


    // you would access the route parameter here and then maybe fetch stuff
    componentDidMount(){
        // console.log("component mounted and started fetching data");

        this.setState({
            post : this.props.data[0],                    
            samples: this.props.data,
            loading:false
        })

        // Setting the title of the browser tab
        document.title = this.props.data[0].standardGeneName + " | YEP"

    }

componentWillReceiveProps(nextProps){

    this.setState({
        post : nextProps.data[0],
        samples: nextProps.data,
        loading:false
    })

    // Setting the title of the browser tab
    document.title = nextProps.data[0].standardGeneName + " | YEP"

 }

//  Custom Handlers
handleSGDClick = () => {
    let url = "https://www.yeastgenome.org/locus/" + this.state.post.sgdId;
    let sgdWin = window.open(url, '_blank');
    sgdWin.focus();
}

handleTrackHubClick = () => {
    // https://stackoverflow.com/questions/94037/convert-character-to-ascii-code-in-javascript
    // retrieve the ascii code of the first letter of the protein, to determine the trackHub group
    const asciiCode = this.state.post.standardGeneName.toUpperCase().charCodeAt(0);
    // console.log(asciiCode);

    let sampleTracks = Config.settings.trackHubPrefix;
    let sampleIds  = '';


    switch(true){
        // A-H
        case (asciiCode >=65 && asciiCode <=72):
            sampleIds = this.state.samples.map(sample=>{
                return "&yep1f"+sample.sampleId+"=full&yep1p"+sample.sampleId+"=dense&yep1r"+sample.sampleId+"=full"
            });
            // console.log(sampleTracks);
            console.log(sampleTracks+sampleIds.join(''));  
            window.open(sampleTracks+sampleIds.join(''), '_blank');
        break;

        // I-Q
        case (asciiCode >=73 && asciiCode <=81):
            sampleIds = this.state.samples.map(sample=>{
                return "&yep2f"+sample.sampleId+"=full&yep2p"+sample.sampleId+"=dense&yep2r"+sample.sampleId+"=full"
            });
            // console.log(sampleTracks);
            console.log(sampleTracks+sampleIds.join(''));  
            window.open(sampleTracks+sampleIds.join(''), '_blank');
        break;

        // R-S
        case (asciiCode >=82 && asciiCode <=83):
            sampleIds = this.state.samples.map(sample=>{
                return "&yep3f"+sample.sampleId+"=full&yep3p"+sample.sampleId+"=dense&yep3r"+sample.sampleId+"=full"
            });
            // console.log(sampleTracks);
            console.log(sampleTracks+sampleIds.join(''));  
            window.open(sampleTracks+sampleIds.join(''), '_blank');
        break;

        // T-Z
        case (asciiCode >=84 && asciiCode <=90):
            sampleIds = this.state.samples.map(sample=>{
                return "&yep4f"+sample.sampleId+"=full&yep4p"+sample.sampleId+"=dense&yep4r"+sample.sampleId+"=full"
            });
            // console.log(sampleTracks);
            console.log(sampleTracks+sampleIds.join(''));  
            window.open(sampleTracks+sampleIds.join(''), '_blank');
        break;
        default:
        break;

    }

    // console.log("handleTrackHubClick() from Sample.js =>  Navigate to Track Hub");    
}

handleDownloadClick = () => {
    console.log("handleDownloadClick() from Sample.js =>  Initiate Download for the entire protein or redirect to bulk downloads");    
}

toggleDrawer = (option) => () => {
    this.setState({
      drawer: option,
    });
  };

    render() {
        // console.log("Rendering POST.js ");

        const { classes } = this.props;
        // Retrieve only the header information, might need to change the sample model for that.
        // Then send all the samples to replicate tabs as props which uses map to create each individual
        // sections, which are themselves individual components

        const { loading, message } = this.state;

        const post = loading ? (
            <Typography component="div" className={classes.center}>                   
                <Typography component="p" variant="subtitle1" >
                    {message}
                </Typography>
                <LinearProgress variant="query" />
            </Typography>
            ) : (
            <div className={classes.card}>

                {/* Header Section */}
                <Card >                    
                        <CardContent >
                            <Grid container spacing={8} alignItems={"center"} >
                                <Grid item sm={"auto"}>
                                    <Typography gutterBottom variant="h4" component="h2">
                                        {this.state.post.standardGeneName}
                                        <Typography component="p" variant="caption" >
                                        {this.state.post.featureName}
                                        </Typography> 
                                    </Typography>                                                              
                                </Grid>                               
                            </Grid>   
                            <Grid container spacing={8} alignItems={"center"}>
                                <Grid item sm={"auto"}>
                                    <Typography component="p" variant="body1" >
                                    {this.state.post.description}
                                    </Typography>
                                </Grid>
                            </Grid>                          
                        </CardContent>
                        <Divider variant="middle"/>

                        {/* Action toolbar */}
                        <CardActions>
                            <Grid container alignItems={"center"} justify={"space-between"}>
                                <Grid item sm={"auto"}>
                                    {/* <Button size="small" color="primary" component={Link} to='/'>Back</Button> */}
                                    <Button size="small" color="primary" onClick={this.handleTrackHubClick}>
                                    <TrackHubIcon className={classes.leftIcon}/>
                                    TrackHub
                                    </Button>
                                    <Tooltip title="Saccharomyces Genome Database" aria-label="Go to Saccharomyces Genome Database">
                                        <Button size="small" color="primary" onClick={this.handleSGDClick}>
                                        <LaunchIcon className={classes.leftIcon} />
                                        view on SGD
                                        </Button>
                                    </Tooltip>
                                    <Button size="small" color="primary" onClick={this.toggleDrawer(true)}>
                                    <InfoIcon className={classes.leftIcon} />
                                        More Info
                                    </Button>
                                </Grid>
                                <Grid item sm={"auto"}>
                                <Button size="small" color="primary"  onClick={this.handleDownloadClick}>
                                    <DownloadIcon className={classes.leftIcon}/>
                                    Download
                                    </Button>
                                </Grid>
                            </Grid>   
                        </CardActions>

                        {/* Drawer to show all the individual sample stats */}
                        <Drawer anchor="top" open={this.state.drawer} onClose={this.toggleDrawer(false)}>
                            <div tabIndex={0} role="button">
                                {/* Sending the toggleDrawer function as a prop that is called inside sampleStats.js */}
                                <SnackbarProvider maxSnack={1} 
                                                  anchorOrigin={{vertical: 'top',horizontal:'right', }} hideIconVariant={true}>
                                    <SampleStats stats={this.state.samples} handleBack={this.toggleDrawer(false)}/>
                                </SnackbarProvider>
                            </div>
                        </Drawer>
                </Card>

            {/* Second Section with Tabs */}
            <ReplicateTabs samples={this.state.samples}/>

            </div>
        )
        return (
            <div>                
                {/* main Sample Content */}
                <Typography component="div" className={classes.center}>
                    {post}
                </Typography>
            </div>
        )
    }
}


Sample.propTypes = {
    classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Sample);
