import React from 'react';
import axios from 'axios';
import clsx from 'clsx';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import Typography from '@material-ui/core/Typography';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Divider from '@material-ui/core/Divider';
import Grid from '@material-ui/core/Grid';
import Collapse from '@material-ui/core/Collapse';
import Config from '../Config';
import TopicStatisticsTable from './TopicStatisticsTable'
import Tooltip from '@material-ui/core/Tooltip';
import DownloadIcon from '@material-ui/icons/GetApp';

const styles = theme => ({
  jumbotron:{
    padding: '1rem 2rem',
    bottomMargin:'2rem',     
},
    center : {
        margin: 'auto',      
        maxWidth: 1100,
        padding: 10,
        // border: '2px solid green'
      },
    card: {
        minWidth: 1200,
        maxWidth: 1200
    },           

      searchbar:{
        width: 1100
      },
      expand: {
        transform: 'rotate(0deg)',
        marginLeft: 'auto',
        transition: theme.transitions.create('transform', {
          duration: theme.transitions.duration.shortest,
        }),
      },
      collapse: {
        transform: 'rotate(0deg)',
        marginLeft: 'auto',
        transition: theme.transitions.create('transform', {
          duration: theme.transitions.duration.shortest,
        }),
      },
      expandOpen: {
        transform: 'rotate(0deg)',
      },
      leftIcon: {
        marginRight: theme.spacing(1)
        // fontSize: 25,
      },  
});



class TopicPage extends React.Component {
  state ={
        topicName :  ' Undefined',
        proteinList: 'Undefined',
        loading: true,
        message: "Fetching",
        expanded: false,
        collapse: false
    }

    handleExpandClick= () =>{
        this.setState({
          expanded: !this.state.expanded
        })
    }
    handleExpand2Click = () => {
      this.setState({
        collapse: !this.state.collapse
        })
    }

    /*handleProteinClick = (protein) =>{        
      let url = (protein == null) ? 
      (  
        this.state.message

        ):
        (Config.settings.appURL + '/protein/'+ protein)
      console.log(url);
        //let proteinWin = window.open(url, '_blank');
        //proteinWin.focus();

}*/


    componentDidMount (){
        const topic = window.location.pathname;
        const topicURL = Config.settings.apiURL +Config.settings.topicsEndpoint +topic;
        console.log(topicURL);

        axios.get(topicURL).then(result=>{
          let topicID = result.data.topic.map(topic =>{
              return topic.topicID
          });
          const proteinList = result.data.topic.map(topic =>{
              return topic.proteinList.split("\t").join(', ')
          });
          this.setState({
            topicName: topicID[0],
            proteinList: proteinList[0],
            loading: false
          });
        }).catch(error =>{
            console.log(error);
            this.setState({
                loading: true,
                message: error.message
            })
        });;    
      }

      render(){  
        const {classes} = this.props;
        const{topicName,proteinList,loading,message,expanded,collapse} = this.state;
    return (
            <div>
            <div className={classes.jumbotron}>
            <Grid container justify = "center">
                <Card className={classes.card}>
                    <CardContent >
                        <Typography gutterBottom variant="h4" component="h2">
                            Topic{topicName}
                        </Typography>
                        <Typography gutterBottom variant="h6" component="h5">
                        Protein List: {proteinList}
                        </Typography>
                        {/*<Grid container spacing={8} alignItems={"center"}>
                                <Grid item sm={"auto"}>
                        <Typography component="p" variant="body1" >

                        </Typography>
                        </Grid>
                        </Grid>*/}

                    </CardContent>
                    <Divider variant="middle"/>


                    <CardActions>
                    {/*<Button size="small">
                    Learn More

                    </Button>*/}
                    <Tooltip title="Wig/Gene List" aria-label="Downloads">
                                        <Button 
                                        size="small" 
                                        color="primary" 
                                        className={clsx(classes.collapse, {[classes.expandOpen]: collapse,})}
                                        onClick = {this.handleExpand2Click}
                                        aria-expanded={this.state.collapse}
                                        >

                                        <DownloadIcon className={classes.leftIcon} />
                                        Downloads
                                        </Button>
                  </Tooltip>

                    <Button
                        size = "small"
                        className={clsx(classes.expand, {[classes.expandOpen]: expanded,})}
                        onClick={this.handleExpandClick}
                        aria-expanded={this.state.expanded}
                        aria-label="Show more"
                    >
                      Learn more

                    </Button>
                  </CardActions>
                      <Collapse in={expanded} timeout="auto" unmountOnExit>
                          <CardContent>
                          <TopicStatisticsTable />
                          {/*<Grid item style={{margin: '0 auto', padding:20}}>                    
                                <TopicStatisticsTable />
                            </Grid>*/}  
                          </CardContent>
                      </Collapse>
                      <Collapse in={collapse} timeout="auto" unmountOnExit>
                          <CardContent>
                            <Button 
                                size="small" 
                                color="primary" 
                                //onClick = {this.handleExpand2Click}
                                >
                                Download Wig file
                            </Button>
                          {/*<Grid item style={{margin: '0 auto', padding:20}}>                    
                                <TopicStatisticsTable />
                            </Grid>*/}  
                          </CardContent>
                      </Collapse>
                </Card>
            </Grid>
                <br/> 
                {/* Sample Explore Table */}

            </div>
            </div>

    );
}}

TopicPage.propTypes = {
    classes: PropTypes.object.isRequired,
  };

  export default withStyles(styles)(TopicPage);
