import React from "react";
import axios from "axios";
import { withRouter} from 'react-router-dom';
import MUIDataTable from "mui-datatables";
import Typography from '@material-ui/core/Typography';
import LinearProgress from '@material-ui/core/LinearProgress';
import Config from '../Config';
import Table from 'react-bootstrap/Table'

class Searchtable extends React.Component {
    handleRowClick = (rowData, rowMeta) => {
        //redirect to Topic page
        window.location.assign(Config.settings.appURL + '/'+ rowData[0]);
    }
    state = {        
        data : [],
        loading : true,
        message: "Fetching Proteins"
    }
    componentDidMount(){
        const topic = window.location.pathname;
        var dataURL = Config.settings.apiURL+Config.settings.topicsEndpoint+topic;;
        axios.get(dataURL)
        .then(res =>{
            //retrieve the protein data
            let proteins = res.data.topic.map(topic=>{
                return topic.proteinList.split("\t")
            });
            //for (var i=0; i< proteins[0].length; i++){
            let values = res.data.topic.map(topic=>{
                //console.log((topic.topicStatisticsTable))
                //return [proteins[0][0],((topic.topicStatisticsTable[0][proteins[0][0]]).split("\t"))]
                return topic.proteinList
            })
            this.setState({
                data : values,
                loading: false
            });
        }).catch(error =>{
            console.log(error);
            this.setState({
                loading: true,
                message: error.message + "/ Server Offline"
            })
        });
      }
    render(){
        const columns = [
        {
            name : "proteins",
            options: {
                filter: false,
                sort: true,

            }
        },
        /*{
            name : "z score",
            options: {
                filter: false,
                sort: true,

            }
        },
        {
            name : "protein peak tags in this topic",
            options: {
                filter: false,
                sort: true,

            }
        },
        {
            name : "total protein peak tags",
            options: {
                filter: false,
                sort: true,

            }
        },
        {
            name : "fraction of peak tags in this topic",
            options: {
                filter: false,
                sort: true,

            }
        },*/
    ];
        const {data, loading, message}= this.state;
        const options = {
            //filterType: "multiselect",
            responsive: "scroll",
            selectableRows: false,
            rowsPerPage: 10,
            viewColumns : false,
            filter: false,
            rowsPerPageOptions:[50,10,15,20,50,100],
            onRowClick: this.handleRowClick,
            print:false,
            //downloadOptions:{filename: 'topicData.csv', separator: ','} 
          };
        const topicsTable = loading ? (  
            <Typography component="div" >                   
                <Typography component="p" variant="subtitle1" >
                    {message}
                </Typography>
                <LinearProgress variant="query" />
            </Typography> 

            ) : (
                <MUIDataTable
                    data={data}
                    columns={columns}
                    options={options}        
                />
            );

        return (
            <Table striped bordered hover size="sm">
  <thead>
    <tr>
      <th>#</th>
      <th>First Name</th>
      <th>Last Name</th>
      <th>Username</th>
    </tr>
  </thead>
  <tbody>
    <tr>
      <td>1</td>
      <td>Mark</td>
      <td>Otto</td>
      <td>@mdo</td>
    </tr>
    <tr>
      <td>2</td>
      <td>Jacob</td>
      <td>Thornton</td>
      <td>@fat</td>
    </tr>
    <tr>
      <td>3</td>
      <td colSpan="2">Larry the Bird</td>
      <td>@twitter</td>
    </tr>
  </tbody>
  </Table>   
        )
    }
}
export default withRouter(Searchtable);
