import React, {Component} from 'react';

// material-ui imports
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import CardActions from '@material-ui/core/CardActions';
import Typography from '@material-ui/core/Typography';
import Button from '@material-ui/core/Button';
import Divider from '@material-ui/core/Divider';
import Grid from '@material-ui/core/Grid';
import LaunchIcon from '@material-ui/icons/Launch';
import TrackHubIcon from '@material-ui/icons/AssessmentOutlined';
import InfoIcon from '@material-ui/icons/InfoOutlined';
import Drawer from '@material-ui/core/Drawer';
import DownloadIcon from '@material-ui/icons/GetApp';
import LinearProgress from '@material-ui/core/LinearProgress';

const styles = theme => ({

  center : {
      margin: 'auto',      
      maxWidth: 1200,
      padding: 10,
      // border: '2px solid green'
    },
  card: {
      maxWidth: 1200
  },           
    leftIcon: {
      marginRight: theme.spacing.unit,
      // fontSize: 25,
    },  
    navbar:{

    },
    searchbar:{
      width: 1100
    } 
});



class TopicPage extends React.Component {
  state ={
    post : null, // setting it to one sample 
    topicName :  null,
    drawer :false,
    topics : null,
    loading: true,
    message: "Fetching Samples"
}

  componentDidMount(){
    /*var getURL = "http://localhost:8080/reviewProteins";
    axios.get(getURL).then(
      res =>{
          console.log(res.data.proteins[1]);
          this.setState({
              proteins:res.data.proteins[1]
          });               
      }
    );*/
    this.setState({
      post : this.props.data[0],                    
      topics: this.props.data,
      loading:false
    })
    document.title = this.props.data[0].topicName + " | YEP"
}
  /*componentWillReceiveProps(nextProps){

  }*/

  render() {

    const { classes } = this.props;
    const { loading, message } = this.state;

    const post = loading ? (
        <Typography component="div" className={classes.center}>                   
            <Typography component="p" variant="subtitle1" >
                {message}
            </Typography>
            <LinearProgress variant="query" />
        </Typography>
        ) : (
        <div className={classes.card}>

            {/* Header Section */}
            <Card >                    
                    <CardContent >
                        <Grid container spacing={8} alignItems={"center"} >
                            <Grid item sm={"auto"}>
                                <Typography gutterBottom variant="h4" component="h2">
                                    {this.state.post.topicName}
                                </Typography>                                                              
                            </Grid>                               
                        </Grid>   
                        <Grid container spacing={8} alignItems={"center"}>
                            <Grid item sm={"auto"}>
                                <Typography component="p" variant="body1" >
                                {this.state.post.proteinContain}
                                </Typography>
                            </Grid>
                        </Grid>                          
                    </CardContent>
                    <Divider variant="middle"/>


            </Card>


        </div>
    )
    return (
        <div>                
            {/* main Sample Content */}
            <Typography component="div" className={classes.center}>
                {post}
            </Typography>
        </div>
    )
}
}



TopicPage.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(TopicPage);
