import React, { Component } from 'react';

// Material ui styling
import { MuiThemeProvider, createMuiTheme } from '@material-ui/core/styles';
import CssBaseline from '@material-ui/core/CssBaseline';
import { BrowserRouter, Route, Switch } from 'react-router-dom';

// SubComponents
import AppContainer from './Components/AppContainer';
import TopicPage from './Components/TopicPage';
// creating a theme with default fontfamily
const theme1 = createMuiTheme({
  typography: { 
    // Use the system font instead of the default Roboto font.
    fontFamily: [
      '"Roboto Slab"',
      '-apple-system',
      'BlinkMacSystemFont',
      '"Segoe UI"',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"',
    ],
     useNextVariants: true },
});

// creating custom theme
const theme2 = createMuiTheme({
  /*palette: {
    primary: {
      light: '#60ad5e',
      main: '#2e7d32',
      dark: '#005005',
      contrastText: '#fff',
    },
    secondary: {
      light: '#ff7961',
      main: '#f44336',
      dark: '#ba000d',
      contrastText: '#fff',
    },
  },*/
  typography: { 
    // Use the system font instead of the default Roboto font.
    fontFamily: [
      '"Bree Serif"',
      '-apple-system',
      'BlinkMacSystemFont',
      '"Segoe UI"',
      'Roboto',
      '"Helvetica Neue"',
      'Arial',
      'sans-serif',
      '"Apple Color Emoji"',
      '"Segoe UI Emoji"',
      '"Segoe UI Symbol"',
    ],
     useNextVariants: true },
});

const styles = theme =>({
  list: {
      width: 900,
    },
  appBar: { 
      background: '#f5f5f5' 
    },
    appBar2: {  
      background: '#fff' 
    },
    drawerHeader:{
      display: 'flex',
      alignItems: 'center',
      padding: '0 8px',
      ...theme.mixins.toolbar,
      justifyContent: 'flex-end',    
      height: 75,
    },
    progress: {
      width: 500
    },
    textField: {
      width: 200,
      margin: "auto"
    },
});


class App extends Component {

  state = {
    isThemeLight: true
  }
  handleThemeChange = () => {
    this.setState({ isThemeLight: !this.state.isThemeLight })
  }
  refresh = () =>{
    this.setState({
      pageData: null
    });
    document.title = "Yeast Topic Project"
    }
    reload = () =>{
      window.location.reload();
    }

  openNewTab = () =>{
      var win = window.open(Config.settings.appURL, '_blank');
      win.focus();
    }

  render() {
    const { isThemeLight } = this.state;
    const {classes} = this.props;
    const{searchOptions, pageData} = this.state;
    const SearchBar = searchOptions
    ? <Search suggestions = {searchOptions} updateContent={this.updateContent}/>
          //showing loading status when not connected to database.
          : <Typography component = 'div'>
            Loading Search Options
            <LinearProgress className={classes.progress}/>
          </Typography>

    const background = isThemeLight ? 'linear-gradient(to bottom,#efefda,#efefda)' : 'linear-gradient(to bottom,#ede6e8,#ede6e8)'
    const Page = pageData ? <TopicPage data={pageData}/>:<PageNoSearch updateContent = {this.updateContent}/>

    return (
      <MuiThemeProvider theme={isThemeLight ? theme1 : theme2}>
      <CssBaseline />
      <div style={{height: '100%', background: background}}>
      <div>
                <Paper square={true} className = {pageData ? classes.appBar2 : classes.appBar}  elevation = {0}>
                <CardActions>
                    <Grid container alignItems = {"center"} justify= {"space-between"}>

                        <Grid item sm = {"auto"}>
                            <Grid container spacing={8} alignItems={"center"} justify={"space-between"}>

                                <Grid item sm={"auto"}>
                                    {SearchBar}
                                </Grid>

                              </Grid>      
                        </Grid>

                        <Grid item sm={"auto"}>
                        {<Tooltip title="Home" aria-label = "home">
                        <IconButton color = "primary" onClick = {this.refresh}>
                            <HomeIcon/>
                        </IconButton>
                        </Tooltip>}

                        {<Tooltip title="Open Topic Website" aria-label="theme">
                        <IconButton color = "primary" onClick = {this.openNewTab}>
                            <TabIcon />
                        </IconButton>
                        </Tooltip>}

                        {<Tooltip title="Change Theme" aria-label="theme">
                        <IconButton color = "primary" onClick = {this.props.changeTheme}>
                            <FormatColorIcon />
                        </IconButton>
                        </Tooltip>}
                    </Grid>
                        </Grid>
                </CardActions>
                </Paper>
                {Page}
            </div>
        <BrowserRouter>
          <Switch>
            {<Route exact path='/' 
            render={() => <AppContainer changeTheme={this.handleThemeChange}/>} />}ï¼Œ
            {/* <Route path="/edit/:sample_id" component={EditSample} /> */}
            {<Route exact path= '/:topicName/' component={TopicPage}/>}
          </Switch>   
        </BrowserRouter>
      </div>        
      </MuiThemeProvider>
    );
  }
}

export default App;
