import React from 'react';
import axios from 'axios';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
//provided components
import Grid from '@material-ui/core/Grid';
import Tooltip from '@material-ui/core/Tooltip';
import Divider from '@material-ui/core/Divider';
import ListItem from '@material-ui/core/ListItem';
import CardActions from '@material-ui/core/CardActions';
import Card from '@material-ui/core/Card';
import CardContent from '@material-ui/core/CardContent';
import HomeIcon from '@material-ui/icons/HomeOutlined';
import TabIcon from '@material-ui/icons/TabOutlined';
import {Paper, IconButton, Typography, LinearProgress} from '@material-ui/core';
import FormatColorIcon from '@material-ui/icons/FormatColorFill';
import MenuItem from '@material-ui/core/MenuItem';
import TextField from '@material-ui/core/TextField';
//sub components
import Search from './Search';
import PageNoSearch from './PageNoSearch';
//configuration
import Config from '../Config';

const styles = theme =>({
    list: {
        width: 900,
      },
    appBar: { 
        background: '#f5f5f5' 
      },
      appBar2: {  
        background: '#fff' 
      },
      drawerHeader:{
        display: 'flex',
        alignItems: 'center',
        padding: '0 8px',
        ...theme.mixins.toolbar,
        justifyContent: 'flex-end',    
        height: 75,
      },
      progress: {
        width: 500
      },
      textField: {
        width: 200,
        margin: "auto"
      },
      center : {
        margin: 'auto',      
        maxWidth: 1200,
        padding: 10,
        // border: '2px solid green'
      },
    card: {
        maxWidth: 1200
    },           
      leftIcon: {
        marginRight: theme.spacing.unit,
        // fontSize: 25,
      },  
      navbar:{

      },
      searchbar:{
        width: 1100
      } 
})

class AppContainer extends React.Component {
    state = {      
        left: false,
        searchOptions : null,    
        pageData: null,
        data: null,
        proteinName: '',
        proteinNames: [],
        message: "Fetching Topics",
        loading: true,
        topics : null,
        post: null

      };

    componentDidMount(){

      this.setState({
        //post : this.props.data[0],                    
        topics: this.props.data,
        loading:false
    })

    // Setting the title of the browser tab
    //document.title = this.props.data[0].topicName + " | YEP"
    console.log(this.props.data);
      const getURL = Config.settings.apiURL + Config.settings.proteinsEndpoint;
      axios.get(getURL).then(res=>{

          let proteinNames = res.data.proteins.map(protein=>{
            return protein.proteinName
        });
        console.log(proteinNames);

        this.setState({
          searchOptions: proteinNames,
        });
        }).catch(err=>{
          console.log(err);        
        });
    }
    reload = () =>{
      window.location.assign(Config.settings.appURL);
      }

    openNewTab = () =>{
        var win = window.open(Config.settings.appURL, '_blank');
        win.focus();
      }
      render(){
          const {classes} = this.props;
          const{searchOptions, pageData,loading, message} = this.state;
          const SearchBar = searchOptions
          ? <Search suggestions = {searchOptions} />
          //showing loading status when not connected to database.
          : <Typography component = 'div'>
            Loading Search Options
            <LinearProgress className={classes.progress}/>
          </Typography>

          const post = loading ? (
            <Typography component="div" className={classes.center}>                   
                <Typography component="p" variant="subtitle1" >
                    {message}
                </Typography>
                <LinearProgress variant="query" />
            </Typography>
            ) : (
            <div className={classes.card}>
                  <Card >                    
                        <CardContent >
                            <Grid container spacing={8} alignItems={"center"} >
                                <Grid item sm={"auto"}>
                                    <Typography gutterBottom variant="h4" component="h2">
                                        {this.state.post.standardGeneName}
                                        <Typography component="p" variant="caption" >
                                        {this.state.post.featureName}
                                        </Typography> 
                                    </Typography>                                                              
                                </Grid>                               
                            </Grid>   
                            <Grid container spacing={8} alignItems={"center"}>
                                <Grid item sm={"auto"}>
                                    <Typography component="p" variant="body1" >
                                    {this.state.post.description}
                                    </Typography>
                                </Grid>
                            </Grid>                          
                        </CardContent>
                        <Divider variant="middle"/>
                  </Card>
            </div>
            )
        return(
            <div>
                <Paper square={true} className = {pageData ? classes.appBar2 : classes.appBar}  elevation = {0}>
                <CardActions>
                    <Grid container alignItems = {"center"} justify= {"space-between"}>

                        <Grid item sm = {"auto"}>
                            <Grid container spacing={8} alignItems={"center"} justify={"space-between"}>

                                {/*SearchBar*/}
                                <Grid item sm={"auto"}>
                                    {SearchBar}
                                </Grid>

                              </Grid>      
                        </Grid>
                        {/*Right top buttons*/}
                        <Grid item sm={"auto"}>
                          {<Tooltip title="Home" aria-label = "home">
                            <IconButton color = "primary" onClick = {this.reload}>
                              <HomeIcon/>
                            </IconButton>
                          </Tooltip>}

                          {<Tooltip title="Open Topic Website" aria-label="theme">
                            <IconButton color = "primary" onClick = {this.openNewTab}>
                              <TabIcon />
                            </IconButton>
                          </Tooltip>}

                          {<Tooltip title="Change Theme" aria-label="theme">
                            <IconButton color = "primary" onClick = {this.props.changeTheme}>
                              <FormatColorIcon />
                            </IconButton>
                          </Tooltip>}
                        </Grid>
                    </Grid>
                </CardActions>
                </Paper>
                <Typography component="div" className={classes.center}>
                    {post}
                </Typography>
            </div>
        )

      }
}

AppContainer.propTypes = {
    classes: PropTypes.object.isRequired,
  };

  export default withStyles(styles)(AppContainer);
