import React from 'react';
import axios from 'axios';
import clsx from 'clsx';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import Typography from '@material-ui/core/Typography';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
import Divider from '@material-ui/core/Divider';
import Grid from '@material-ui/core/Grid';
import Config from '../Config';
import Try from './try';
const styles = theme => ({
  jumbotron:{
    padding: '1rem 2rem',
    bottomMargin:'2rem',     
},
    center : {
        margin: 'auto',      
        maxWidth: 1100,
        padding: 10,
        // border: '2px solid green'
      },
    card: {
        minWidth: 1200
    },           

      searchbar:{
        width: 1100
      },


});



class TopicGraphPage extends React.Component {
  state ={
        topicTagCountsInSubsectors: null,
        loading: true,
        message: "Fetching Topics",
    }

    componentDidMount (){
        const topic = window.location.pathname;
        const topicURL = Config.settings.apiURL +Config.settings.topicsEndpoint +topic;

        axios.get(topicURL).then(result=>{
          const topicTagCountsInSubsectors = result.data.topic.map(topic =>{
              return topic.topicTagCountsInSubsectors
          });
          this.setState({
            topicTagCountsInSubsectors: topicTagCountsInSubsectors[0],
            loading: false
          });
          console.log(topicTagCountsInSubsectors)
          //const Page = <Try why = {topicTagCountsInSubsectors}/>
        }).catch(error =>{
            console.log(error);
            this.setState({
                loading: true,
                message: error.message
            })
        });;    
      }

      render(){  
        const {classes} = this.props;
        const{topicTagCountsInSubsectors,loading,message,} = this.state;
        const Page = <Try why = {this.state.topicTagCountsInSubsectors}/>
        console.log(this.state.topicTagCountsInSubsectors)
        //const Page = <Try why = {this.state.topicTagCountsInSubsectors}/>
    return (
        <div className={classes.root}>
            <Grid container justify = "center">
                <Card className={classes.card}>
                    <CardContent >
                    <Typography>
                        </Typography>
                    </CardContent>
                    <Divider variant="middle"/>

                    {Page}
                </Card>
            </Grid>
                <br/> 
                {/* Sample Explore Table */}

            </div>

    );
}}

TopicGraphPage.propTypes = {
    classes: PropTypes.object.isRequired,
  };

  export default withStyles(styles)(TopicGraphPage);
