import React from "react";
import axios from "axios";
import { withRouter} from 'react-router-dom';
import MUIDataTable from "mui-datatables";
import Typography from '@material-ui/core/Typography';
import LinearProgress from '@material-ui/core/LinearProgress';
import Config from '../Config';

class Datatable extends React.Component {
    handleRowClick = (rowData, rowMeta) => {
        this.props.updateContent(rowData[1])();
    }

    state = {
        data : [],
        loading : true,
        message: 'Getting Topics'
    }

    componentDidMount(){
        var dataURL = Config.settings.apiURL+Config.settings.proteinsEndpoint;
        axios.get(dataURL)
        .then(res =>{
            console.log(res.data.proteins);
            //retrieve the protein data
            let values = res.data.proteins.map(protein=>{
                return [protein.Topic_belong]
            });
            this.setState({
                data : values,
                loading: false
            });
        }).catch(error =>{
            console.log(error);
            this.setState({
                loading: true,
                message: error.message + "/ Server Offline"
            })
        });
    }
    render(){
        const columns = [
            {
            name : "Topic Name",
            options: {
                filter: false,
                sort: true,

            }
        },
        {
            name : "Protein Number",
            options: {
                filter: false,
                sort: true,

            }
        },
    ];
        const { data, loading, message } = this.state;
        const options = {
            //filterType: "multiselect",
            responsive: "scroll",
            selectableRows: false,
            rowsPerPage: 10,
            viewColumns : false,
            filter: false,
            //rowsPerPageOptions:[50,10,15,20,50,100],
            onRowClick: this.handleRowClick,
            print:false,
            downloadOptions:{filename: 'topicData.csv', separator: ','} 
          };

          const topicsTable = loading ? (  
            <Typography component="div" >                   
                <Typography component="p" variant="subtitle1" >
                    {message}
                </Typography>
                <LinearProgress variant="query" />
            </Typography> 

            ) : (
                <MUIDataTable
                    title={"Explore"}
                    data={data}
                    columns={columns}
                    options={options}        
                />
            );

        return (
            <div style={{maxWidth: 500, margin: '0 auto'}}>
             {topicsTable}
            </div>

        )
    }
}
export default withRouter(Datatable);
