import React from 'react';
import axios from 'axios';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';

// Material components
import Drawer from '@material-ui/core/Drawer';
import Grid from '@material-ui/core/Grid';
import Tooltip from '@material-ui/core/Tooltip';
import Divider from '@material-ui/core/Divider';
import ListItem from '@material-ui/core/ListItem';
import MenuIcon from '@material-ui/icons/Menu';
import ChevronLeftIcon from '@material-ui/icons/ChevronLeft';
import CardActions from '@material-ui/core/CardActions';
import HomeIcon from '@material-ui/icons/HomeOutlined';
import TabIcon from '@material-ui/icons/TabOutlined';
import {Paper, IconButton, Typography} from '@material-ui/core';
import FormatColorIcon from '@material-ui/icons/FormatColorFill';
import LinearProgress from '@material-ui/core/LinearProgress';
import MenuItem from '@material-ui/core/MenuItem';
import TextField from '@material-ui/core/TextField';

// Sub components
import Search from './Search';
import LandingPage from './LandingPage';
import Sample from './Sample';

// Configuration 
import Config from '../Config';


const styles = theme => ({
  list: {
    width: 375,
  },
  appBar: { 
    background: '#eeeeee' 
  },
  appBar2: {  
    background: '#fff' 
  },
  drawerHeader:{
    display: 'flex',
    alignItems: 'center',
    padding: '0 8px',
    ...theme.mixins.toolbar,
    justifyContent: 'flex-end',    
    height: 75,
  },
  progress: {
    width: 500
  },
  textField: {
    width: 200,
    margin: "auto"
  },

});

class AppContainer extends React.Component {
  state = {    
    left: false,
    searchOptions : null,    
    pageData: null,
    data: null,
    treatment : '',
    publicFilter: '',
    treatments:[],
    sampleStatus: ['Public', 'Private', 'All']
  };

  toggleDrawer = (side, open) => () => {
    this.setState({
      [side]: open,
    });
  };

  componentDidMount(){
    const aliasURL = Config.settings.apiURL + Config.settings.aliasesEndpoint + "/" + Config.settings.siteAvailability;

    // fetch aliases
    axios.get(aliasURL).then(res=>{
        //  console.log(res.data.aliases); 
         this.setState({
          searchOptions: res.data.aliases
        })         
      }).catch(err=>{
        console.log(err);        
      });
  }

  updateContent = (protein) => () =>{
    const sampleURL = Config.settings.apiURL +Config.settings.samplesEndpoint + "/"+protein.toUpperCase();
    console.log(sampleURL);


    // fetch sampleData 
    axios.get(sampleURL).then(result=>{
      const treatments = [...new Set(result.data.samples.map(sample => {
        return sample.treatments;
        } ))]; 

      this.setState({
        pageData: result.data.samples,
        data: result.data,
        treatments: [...treatments, 'All']
      });
    });    
  }

  refresh = () =>{
    this.setState({
      pageData: null
    });

    document.title = "Yeast Epigenome Project | YEP"
  }

  reload = () =>{
    window.location.reload();
  }

  openNewTab = () =>{
    var win = window.open(Config.settings.appURL, '_blank');
    win.focus();
  }

  //  handles one filtering step, how can you do multiple?
  handleTreatmentFilter = name => event => {

    // when all is selected , reset the pagedata
    if (event.target.value === 'All'){      
      this.setState({
        pageData: this.state.data.samples,
        [name] : event.target.value
      })
    }
    else{
      // filter the pageData for the selected sample
      const filteredData = this.state.data.samples.filter(sample => sample.treatments === event.target.value);

      this.setState({
        pageData: filteredData,
        [name] : event.target.value
      })

    }   
  };

  //  handles one filtering step, how can you do multiple?
  handleIsPublicFilter = name => event => {

    // when all is selected , reset the pagedata
    if (event.target.value === 'All'){      
      this.setState({
        pageData: this.state.data.samples,
        [name] : event.target.value
      })
    }
    else{      
      // filter the pageData for the selected sample
      const filteredData = event.target.value === 'Public' ? this.state.data.samples.filter(sample => sample.isPublic === true) : this.state.data.samples.filter(sample => sample.isPublic === false);


      this.setState({
        pageData: filteredData,
        [name] : event.target.value
      })

    }   

  };


  render() {
    const { classes } = this.props;
    const {searchOptions, pageData} = this.state;

    // Render the searchbar once you get the data
    const SearchBar = searchOptions 
    ? <Search suggestions={searchOptions} updateContent={this.updateContent}/> 
    :  <Typography component='div'>
        Loading Search Options
        <LinearProgress className={classes.progress}/>
      </Typography>

    // set the content for the page based on the page data
    const Page = pageData ? <Sample data={pageData}/> : <LandingPage updateContent={this.updateContent}/> 

    // Seach filter options
    const filterOptions = pageData ? 
    <div>
      <br/>
    <ListItem >
      <TextField
        id="select-celltype"
        select
        label="Treatment"
        variant="outlined"
        className={classes.textField}
        value={this.state.treatment}
        onChange={this.handleTreatmentFilter('treatment')}
        SelectProps={{
          MenuProps: {
            className: classes.menu,
          },
        }}
        margin="normal"
      >
        {this.state.treatments.map(option => (
          <MenuItem key={option} value={option}>
            {option}
          </MenuItem>
        ))}
      </TextField>
    </ListItem>
    </div> : ( <div>
      <br/>     
    <ListItem >
      <TextField
        disabled
        id="select-treatment"
        select
        label="Treatment"
        variant="outlined"
        className={classes.textField}
        value={this.state.treatment}
        onChange={this.handleTreatmentFilter('treatment')}
        SelectProps={{
          MenuProps: {
            className: classes.menu,
          },
        }}
        margin="normal"
      >
        {this.state.treatments.map(option => (
          <MenuItem key={option} value={option}>
            {option}
          </MenuItem>
        ))}
      </TextField>
    </ListItem>   
    <br/>
    <Divider/>
    <ListItem>
      <Typography style={{margin: '0 auto'}}>Search a protein to enable filtering </Typography>
      </ListItem>
    </div>)

    // Main Drawer section
    const drawerMenu = (
      <div className={classes.list}>
          {/* Header */}
          <div className={classes.drawerHeader}>
            <Grid container alignItems={"center"} justify={"space-between"}>
              <Grid item>
              </Grid>
              <Grid item >
                <Typography variant="h6">
                   Search Options
                 </Typography>
              </Grid>
              <Grid item>
                <Tooltip title="Close Menu" aria-label="Close menu" >                        
                  <IconButton color="primary" onClick={this.toggleDrawer('left', false)}>
                    <ChevronLeftIcon /> 
                  </IconButton>
                </Tooltip> 
              </Grid>
            </Grid>
          </div>

          <Divider/>

       {filterOptions}

      </div>
    );

    return (
      <div>
        {/* Top SearchBar */}
        <Paper square={true} className={pageData ? classes.appBar2 : classes.appBar} elevation={0} >
          <CardActions>
            <Grid container alignItems={"center"} justify={"space-between"}>

              <Grid item sm={"auto"}>
                  <Grid container spacing={8} alignItems={"center"} justify={"space-between"}>
                    {/* Filter Menu */}
                    <Grid item sm={"auto"}>
                      <Tooltip title="Filter Menu" aria-label="filter menu">                  
                        <IconButton color="primary" aria-label="Open drawer"
                        onClick={this.toggleDrawer('left', true)}>
                          <MenuIcon />
                        </IconButton>
                      </Tooltip>   
                    </Grid> 
                    {/* Seachbar */}
                    <Grid item sm={"auto"}>
                        {SearchBar}
                    </Grid>                         
                  </Grid>      
              </Grid>

              <Grid item sm={"auto"}>
              {/* just a place holder to separate the icons */}
              </Grid>

              <Grid item sm={"auto"}>               
                <Tooltip title="Home" aria-label="home">                 
                  <IconButton color="primary" onClick={this.refresh}>
                      <HomeIcon />
                  </IconButton>                 
                </Tooltip>
                <Tooltip title="Open YEP Website" aria-label="theme">
                  <IconButton color="primary" onClick={this.openNewTab}>
                    <TabIcon />
                  </IconButton>
                </Tooltip>
                <Tooltip title="Change Theme" aria-label="theme">
                  <IconButton color="primary" onClick={this.props.changeTheme}>
                    <FormatColorIcon />
                  </IconButton>
                </Tooltip> 
              </Grid>

            </Grid>   
          </CardActions>             
        </Paper>

        <Drawer open={this.state.left} onClose={this.toggleDrawer('left', false)}>
          <div
            tabIndex={0}
            role="button"            
            onKeyPress={this.toggleDrawer('left', false)}             
          >
            {drawerMenu}
          </div>
        </Drawer>

        {/* Main Page content */}       
          {Page}



      </div>
    );
  }
}

AppContainer.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(AppContainer);
