import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import Typography from '@material-ui/core/Typography';
import CardContent from '@material-ui/core/CardContent';
import Divider from '@material-ui/core/Divider';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';

class MotifHitTable extends React.Component {
    state = {
        selectTab :0,
        singleMotif:this.props.topic.motif[0][1]
      }

    componentDidMount(){
        var dataURL = Config.settings.apiURL+Config.settings.topicsEndpoint;
        axios.get(dataURL)
        .then(res =>{
            console.log(res.data.topics);
            //retrieve the protein data
            let values = res.data.topics.map(topic=>{
                console.log([topic.topicID, (topic.proteinList.split('\t')).length])
                return [topic.topicID, (topic.proteinList.split('\t')).length]
            });
            this.setState({
                data : values,
                loading: false
            });
        }).catch(error =>{
            console.log(error);
            this.setState({
                loading: true,
                message: error.message + "/ Server Offline"
            })
        });
      }
    render(){
        const columns = [
            {
            name : "Topic Name",
            options: {
                filter: false,
                sort: true,

            }
        },
        {
            name : "Protein Number",
            options: {
                filter: false,
                sort: true,

            }
        },
    ];
        const {data, loading, message}= this.state;
        const options = {
            //filterType: "multiselect",
            responsive: "scroll",
            selectableRows: false,
            rowsPerPage: 10,
            viewColumns : false,
            filter: false,
            rowsPerPageOptions:[50,10,15,20,50,100],
            onRowClick: this.handleRowClick,
            print:false,
            downloadOptions:{filename: 'topicData.csv', separator: ','} 
          };
        const topicsTable = loading ? (  
            <Typography component="div" >                   
                <Typography component="p" variant="subtitle1" >
                    {message}
                </Typography>
                <LinearProgress variant="query" />
            </Typography> 

            ) : (
                <MUIDataTable
                    title={"Explore"}
                    data={data}
                    columns={columns}
                    options={options}        
                />
            );

        return (
            <div style={{maxWidth: 1140, margin: 'auto'}}>
             {topicsTable}
            </div>

        )
    }
}

export default withRouter(MotifHitTable);
