import React from 'react';
import axios from 'axios';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
//provided components
import Grid from '@material-ui/core/Grid';
import Tooltip from '@material-ui/core/Tooltip';
import Divider from '@material-ui/core/Divider';
import ListItem from '@material-ui/core/ListItem';
import CardActions from '@material-ui/core/CardActions';
import HomeIcon from '@material-ui/icons/HomeOutlined';
import TabIcon from '@material-ui/icons/TabOutlined';
import {Paper, IconButton, Typography} from '@material-ui/core';
import FormatColorIcon from '@material-ui/icons/FormatColorFill';
import MenuItem from '@material-ui/core/MenuItem';
import TextField from '@material-ui/core/TextField';
//sub components
import PageNoSearch from './PageNoSearch';
//configuration
import Config from '../Config';

const styles = theme =>({
    appBar: { 
        background: '#fffacd' 
      },
      progress: {
        width: 500
      },
      textField: {
        width: 200,
        margin: "auto"
      },
})

class AppContainer extends React.Component {
    state = {      
      pageData: null,
      data: null,
      treatment : '',
      publicFilter: '',
      treatments:[],
      sampleStatus: ['Public', 'Private', 'All']
    };

    componentDidMount(){
    const getURL = Config.settings.apiURL;
      axios.get(getURL).then(
        res =>{
            console.log(res.data.sample[0]);
            this.setState({
                samples:res.data.sample[0]
            })
        }).catch(err=>{
            console.log(err);
        });            
        }
    refresh = () =>{
    this.setState({
      pageData: null
    });

    document.title = "Yeast Topic Project"
    }
    reload = () =>{
        window.location.reload();
      }
      openNewTab = () =>{
        var win = window.open(Config.settings.appURL, '_blank');
        win.focus();
      }
      render(){
          const {classes} = this.props;
          const{searchOptions, pageData} = this.state;
          const SearchBar = searchOptions
          const Page = <PageNoSearch updateContent = {this.uodateContent}/>
        return(
            <div>
                <Paper square={true} className = {classes.appBar} elevation = {0}>
                <CardActions>
                    <Grid container alignItems = {"center"} justify= {"space-between"}>
                        <Grid item sm={"auto"}>
                            {SearchBar}
                        </Grid>
                    </Grid>
                    <Grid>
                        {<Tooltip title="Home" aria-label = "home">
                        <IconButton color = "#26a69a" onClick = {this.refresh}>
                            <HomeIcon/>
                        </IconButton>
                        </Tooltip>}

                        {<Tooltip title="Open Topic Website" aria-label="theme">
                        <IconButton color = "#26a69a" onClick = {this.openNewTab}>
                            <TabIcon />
                        </IconButton>
                        </Tooltip>}

                        {<Tooltip title="Change Theme" aria-label="theme">
                        <IconButton color = "#2e7d32" onClick = {this.props.changeTheme}>
                            <FormatColorIcon />
                        </IconButton>
                        </Tooltip>}
                    </Grid>
                </CardActions>
                </Paper>
                {Page}
            </div>
        )

      }
}

AppContainer.propTypes = {
    classes: PropTypes.object.isRequired,
  };

  export default withStyles(styles)(AppContainer);
