import React from "react";
import axios from "axios";
import { withRouter} from 'react-router-dom';
import MUIDataTable from "mui-datatables";
import Typography from '@material-ui/core/Typography';
import LinearProgress from '@material-ui/core/LinearProgress';
import Config from '../Config';

class TopicStatisticsTable extends React.Component {
    handleRowClick = (rowData, rowMeta) => {
        //redirect to Topic page
        window.location.assign(Config.settings.appURL + '/protein/'+ rowData[0]);
    }

    componentDidMount(){
        const topic = window.location.pathname;
        var dataURL = Config.settings.apiURL+Config.settings.topicsEndpoint+topic;
        axios.get(dataURL)
        .then(res =>{
            //retrieve the protein data
            let values = res.data.topic.map(topic=>{
                var dict = res.data.topic[0]["topicStatisticsTable"][0];
                var arr = [];
                for (var key in dict) {
                    if (dict.hasOwnProperty(key)) {
                        arr.push( key, dict[key].split("\t")[0]);
                        for(var i=1; i<dict[key].split("\t").length; i++){
                        arr.push(dict[key].split("\t")[i]);
                        }
                    }
                }
                console.log(arr)
                return [topic.topicID, (topic.proteinList.split('\t')).length]
            });
            /*this.setState({
                data : values,
                loading: false
            });
        }).catch(error =>{
            console.log(error);
            this.setState({
                loading: true,
                message: error.message + "/ Server Offline"
            })
        });*/
      })}

    render(){

    const data = [
        ["Reb1", "13.2", "153682", "154340","1.00"],
        ["Rvb1", "9.1", "7831", "17901","0.44"],
       ];
       const options = {
        //filterType: "multiselect",
        responsive: "scroll",
        selectableRows: false,
        rowsPerPage: 10,
        viewColumns : false,
        filter: false,
        rowsPerPageOptions:[50,10,15,20,50,100],
        onRowClick: this.handleRowClick,
        print:false,
        //downloadOptions:{filename: 'topicData.csv', separator: ','} 
      };

      const columns = ["proteins", "z-score", "protein peak tags in this topic", "total protein peak tags","fraction of peak tags in this topic"];


      return (
        <div>
            <MUIDataTable
              data={data}
              columns={columns}
              options={options} 
            />
        </div>      
  )
}
}
export default withRouter(TopicStatisticsTable);
