import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import Typography from '@material-ui/core/Typography';
import CardContent from '@material-ui/core/CardContent';
import Divider from '@material-ui/core/Divider';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';

import comingup from '../comingup.png';

const styles = theme => ({
  jumbotron:{
    padding: '1rem 1rem',
    bottomMargin:'1rem',     
},
  tagpaper:{
      width: 150
  },
    center : {
        margin: 'auto',      
        maxWidth: 1100,
        padding: 5,
        // border: '2px solid green'
      },
    card: {
        maxWidth: 1100,
        minWidth: 1100
    },    
    largecard: {
      maxWidth: 1200,
      minWidth: 1200
  },      
    featureHeatmap:{
      width: 320,
      height:150,
      marginTop:20,
      marginBottom:20,
    //   border: '2px solid yellow'
  },  
  featureHeatmapbar:{
      width: 100,
      height:180,
      marginTop:20,
      marginBottom:20,

  },
  myColor:{
    background: 'black'
  },
      searchbar:{
        width: 1100
      },
    scroller:{
      overflow: "hidden"
    },
      mainContainer:{
          overflow: 'scroll'
      },
      colorBar:{
        height: 470,
        width: 20,
        marginTop:14,
        marginLeft: -15   
      },
      root: {
        width: '100%',
        marginTop: theme.spacing(3),
        overflowX: 'auto',
      },
      table: {
        minWidth: 650,
      },
});

function createData(name, calories, fat, carbs, protein) {
  return { name, calories, fat, carbs, protein };
}
const rows = [
  createData('Frozen yoghurt', 159, 6.0, 24, 4.0),
  createData('Ice cream sandwich', 237, 9.0, 37, 4.3),
  createData('Eclair', 262, 16.0, 24, 6.0),
  createData('Cupcake', 305, 3.7, 67, 4.3),
  createData('Gingerbread', 356, 16.0, 49, 3.9),
];

class Motif extends React.Component { 
  state = {
    selectTab :0,
    singleMotif:this.props.topic.motif[0][1]
  }

  /*componentDidMount(){
    this.setState({
      selectTab :0,
      bindingRegionProfiles: this.props.topic.bindingRegionProfiles[0],
      tssProfiles: this.props.topic.tssProfiles[0],
      tesProfiles : this.props.topic.tesProfiles[0],
        //stringPicture: this.props.topic.stringPicture
});}*/

  componentWillReceiveProps(nextProps){
    this.setState({
      selectTab :0,
      singleMotif: nextProps.topic.motif[0][1]
        //stringPicture: nextProps.topic.stringPicture
  });
  }

  /*handleChange = (event, selectedTab) => {

    selectedTab === 0 ? this.setState({
        selectedTab: selectedTab,
        imageURL: this.props.topic.bindingRegionProfiles[0]
    }) : 
    this.setState({
        selectedTab: selectedTab,
        imageURL: this.props.topic.tssProfiles[0]
    })
  };*/
  handleChange = (event, selectedTab) => {

    switch(selectedTab) {
        case 1:
            this.setState({
              selectTab: selectedTab,
              singleMotif: this.props.topic.motif[0][selectedTab+1]
            });
          break;
        case 2:
        this.setState({
            selectTab: selectedTab,
            singleMotif: this.props.topic.motif[0][selectedTab+1]
        })
        break;
        default:
            this.setState({
              selectTab: selectedTab,
              singleMotif: this.props.topic.motif[0][1]
            })
      }
  };

  render(){ 
    const {classes} = this.props;
    const {singleMotif} = this.state
    //let heatmap3 = proteinList.map((protein)=>("http://localhost:8080/" + imageURL.heatmap3category[0][protein]));
    console.log(this.props.topic.motif[0])
    console.log(this.props.topic.motif)
    //Get number of motifs and tag them
    var motifNum = [];
    if(this.props.topic.motif !== []){
    for (var key in this.props.topic.motif[0]) {
      if (this.props.topic.motif[0].hasOwnProperty(key)) {
          motifNum.push(key)
          }
      }
    }
    console.log(motifNum)
    var tabList = (motifNum.length)=== 0 ?
    <Tab label={"Motif "}  key={0}/>
    :motifNum.map(item=>(
    <Tab label={"Motif "+item}  key={item-1}/>
    ))

    //render single motif materials, If no motif then show "no motifs"

    const heatmap3category = (motifNum.length)=== 0 ?
      (<CardContent> 
          <Typography>
            No Motif to show
          </Typography>
        </CardContent>)
    :(
      <Grid container 
            direction="column"
            justify="flex-start"
            alignItems="flex-start"
            spacing={0}
            className={classes.mainContainer}
            >
                <Grid item >
                  <img src={"http://localhost:8080/"+singleMotif.logo} alt="Motif logo"
                  className={classes.featureHeatmap}/>
                </Grid>
                <Divider/>
                <Grid item>
                <Table className={classes.table}>
                    <TableHead>
                      <TableRow>
                        <TableCell>Dessert (100g serving)</TableCell>
                        <TableCell align="right">Calories</TableCell>
                        <TableCell align="right">Fat&nbsp;(g)</TableCell>
                        <TableCell align="right">Carbs&nbsp;(g)</TableCell>
                        <TableCell align="right">Protein&nbsp;(g)</TableCell>
                      </TableRow>
                    </TableHead>
                    <TableBody>
                      {rows.map(row => (
                        <TableRow key={row.name}>
                          <TableCell component="th" scope="row">
                            {row.name}
                          </TableCell>
                          <TableCell align="right">{row.calories}</TableCell>
                          <TableCell align="right">{row.fat}</TableCell>
                          <TableCell align="right">{row.carbs}</TableCell>
                          <TableCell align="right">{row.protein}</TableCell>
                        </TableRow>
                      ))}
                    </TableBody>
                  </Table>
                  </Grid>
      </Grid>
    )

    return (
            <div className={classes.jumbotron}>
                <Grid container justify = "center">
                  <Paper elevation={0} className={classes.card}>
                        <Typography variant="h5" paragraph={true}>
                                Motifs
                            </Typography>

                    <Paper elevation={4}>  
                        <Tabs
                          value={this.state.selectTab}
                          onChange={this.handleChange}
                          variant="scrollable"
                          scrollButtons="on"
                          textColor="primary" 
                          classes={{scrollable:classes.scroller, indicator:classes.myColor}}  
                        >
                          {tabList}
                        </Tabs>
                        <Divider/>

                    <CardContent className = {classes.card}>   
                      <Typography component="div" >      
                              {/*tags if contained*/}                 
                              <Grid container direction="row" ></Grid>

                                  {heatmap3category}


                        </Typography>          
                      </CardContent>
                      </Paper> 
                      </Paper> 



                </Grid>
            </div>      


    );
}}

Motif.propTypes = {
    classes: PropTypes.object.isRequired,
  };

  export default withStyles(styles)(Motif);
