import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import Typography from '@material-ui/core/Typography';
import CardContent from '@material-ui/core/CardContent';
import Divider from '@material-ui/core/Divider';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';

import comingup from '../comingup.png';

const styles = theme => ({
  jumbotron:{
    padding: '1rem 1rem',
    bottomMargin:'1rem',     
},
  tagpaper:{
      width: 150
  },
    center : {
        margin: 'auto',      
        maxWidth: 1100,
        padding: 5,
        // border: '2px solid green'
      },
    card: {
        maxWidth: 1100,
        minWidth: 1100
    },    
    largecard: {
      maxWidth: 1200,
      minWidth: 1200
  },      
    featureHeatmap:{
      width: 150,
      height:180,
      marginTop:20,
      marginBottom:20,
    //   border: '2px solid yellow'
  },  
  featureHeatmapbar:{
      width: 10,
      height:180,
      marginTop:20,
      marginBottom:20,

  },
  myColor:{
    background: 'black'
  },
      searchbar:{
        width: 1100
      },
    scroller:{
      overflow: "hidden"
    },
      mainContainer:{
          overflow: 'scroll'
      },
      colorBar:{
        height: 470,
        width: 20,
        marginTop:14,
        marginLeft: -15   
      },
});


class Motif extends React.Component { 
  state = {
    selectTab :0,
    singleMotif:this.props.topic.motif[0]
  }

  /*componentDidMount(){
    this.setState({
      selectTab :0,
      bindingRegionProfiles: this.props.topic.bindingRegionProfiles[0],
      tssProfiles: this.props.topic.tssProfiles[0],
      tesProfiles : this.props.topic.tesProfiles[0],
        //stringPicture: this.props.topic.stringPicture
});}*/

  componentWillReceiveProps(nextProps){
    this.setState({
      selectTab :0,
      singleMotif: nextProps.topic.motif[0]
        //stringPicture: nextProps.topic.stringPicture
  });
  }

  /*handleChange = (event, selectedTab) => {

    selectedTab === 0 ? this.setState({
        selectedTab: selectedTab,
        imageURL: this.props.topic.bindingRegionProfiles[0]
    }) : 
    this.setState({
        selectedTab: selectedTab,
        imageURL: this.props.topic.tssProfiles[0]
    })
  };*/
  handleChange = (event, selectedTab) => {

    switch(selectedTab) {
        case 1:
            this.setState({
              selectTab: selectedTab,
              singleMotif: this.props.topic.motif[0]
            });
          break;
        case 2:
        this.setState({
            selectTab: selectedTab,
            singleMotif: this.props.topic.motif[0]
        })
        break;
        default:
            this.setState({
              selectTab: selectedTab,
              singleMotif: this.props.topic.motif[0]
            })
      }
  };

  render(){ 
    const {classes} = this.props;
    console.log(this.props.topic.motif[0])
    const {singleMotif} = this.state;
    //let heatmap3 = proteinList.map((protein)=>("http://localhost:8080/" + imageURL.heatmap3category[0][protein]));
    var motifNum = [];
    for (var key in singleMotif) {
      if (singleMotif.hasOwnProperty(key)) {
          motifNum.push(key)
          }
      }
    var tabList = (this.props.topic.motif).length == 0 ?
    <Tab label={"Motif "}  key={0}/>
    :motifNum.map(item=>(
    <Tab label={"Motif "+item}  key={item-1}/>
    ))
    const heatmap3category = (this.props.topic.motif).length == 0 ?
      (<CardContent elevation = {8}> 
          <Typography>
            No Motif to show
          </Typography>
        </CardContent>)
    :(<CardContent > 
          <Typography>
            You got motifs
          </Typography>
      </CardContent>)
      console.log(singleMotif)

    return (
            <div className={classes.jumbotron}>
                <Grid container justify = "center">
                  <Paper elevation={0} className={classes.card}>
                        <Typography variant="h5" paragraph={true}>
                                Motifs
                            </Typography>

                    <Paper elevation={4}>  
                        <Tabs
                          value={this.state.selectTab}
                          onChange={this.handleChange}
                          variant="scrollable"
                          scrollButtons="on"
                          textColor="primary" 
                          classes={{scrollable:classes.scroller, indicator:classes.myColor}}  
                        >
                          {tabList}
                        </Tabs>
                        <Divider/>

                    <CardContent className = {classes.card}>   
                      <Typography component="div" >      
                              {/*tags if contained*/}                 
                              <Grid container direction="row" ></Grid>

                            {heatmap3category}
                        </Typography>          
                      </CardContent>
                      </Paper> 
                      </Paper> 



                </Grid>
            </div>      


    );
}}

Motif.propTypes = {
    classes: PropTypes.object.isRequired,
  };

  export default withStyles(styles)(Motif);
