import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Typography from '@material-ui/core/Typography';
import CardContent from '@material-ui/core/CardContent';
import Divider from '@material-ui/core/Divider';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';
import Config from '../Config.js'

const styles = theme => ({
  jumbotron:{
    padding: '3rem 3rem',
    bottomMargin:'1rem',     
},
  tagpaper:{
      width: 150,
      overflowX: 'scroll',
      marginTop: 10
  },
    card: {
        width: 1100,
        marginTop:30,

    },    
    pcard: {
      //maxWidth: 1100,
      width: 1100,
      overflow: 'scroll'
  },   
  scrolltagcard:{
    //minWidth: 10000,
    //minWidth: 1000,
    overflowX : "auto",
    maxHeight: 25,
    //minHeight: 25
},  
    largecard: {
      width: 1200,
  },      
    featureHeatmap:{
      width: 150,
      height:180,
      marginTop:20,
      marginBottom:20,
    //   border: '2px solid yellow'
  },  
  scroll1card:{
    //maxWidth: 10000,
    //minWidth: 10000,
    //overflowY : "hidden",
    overflowX: "auto",
    maxHeight: 230,
  },
  featureHeatmapbar:{
      width: 10,
      height:180,
      marginTop:20,
      marginBottom:20,

  },
  myColor:{
    background: 'black'
  },
      searchbar:{
        width: 1100
      },
    scroller:{
      overflowX: 'scroll'
    },
      colorBar:{
        height: 470,
        width: 20,
        marginTop:14,
        marginLeft: -15   
      },
      mytags:{
        overflow: 'scroll'
      }

});


class Profiles extends React.Component { 
  state = {
    selectTab :0,
    imageURL:this.props.topic.bindingRegionProfiles[0]
  }


  componentWillReceiveProps(nextProps){
    this.setState({
      selectTab :0,
      imageURL: nextProps.topic.bindingRegionProfiles[0]
        //stringPicture: nextProps.topic.stringPicture
  });
  }

  handleChange = (event, selectedTab) => {

    switch(selectedTab) {
        case 1:
          console.log(selectedTab);

            this.setState({
              selectTab: selectedTab,
              imageURL: this.props.topic.tssProfiles[0]
            });
          break;
        case 2:
        this.setState({
            selectTab: selectedTab,
            imageURL: this.props.topic.tesProfiles[0]
        })
        break;
        default:
            this.setState({
              selectTab: selectedTab,
              imageURL: this.props.topic.bindingRegionProfiles[0]
            })
      }
  };

  render(){ 
    const {classes} = this.props;
    //const{tabContent} = this.state;
    const {imageURL} = this.state;
    const topicID = this.props.topic.topicID;
    const proteinList = this.props.topic.proteinList.split('\t');
    console.log(proteinList)
    let averagePics = proteinList.map(protein =>(
      Config.settings.apiURL +'/'+ imageURL.averagePlot[0][protein]
    ));
    let heatmapPics= proteinList.map((protein)=>(Config.settings.apiURL +'/' + imageURL.heatmap[0][protein]));
    let heatmap3 = proteinList.map((protein)=>(Config.settings.apiURL +'/' + imageURL.heatmap3category[0][protein]));

    const heatmap3category = this.state.selectTab === 0
        ?(<CardContent > 
          <Typography>
            No heatmap3category pics
          </Typography>
          </CardContent>)
        :(                   


            <Grid direction="row">
            <img src={Config.settings.apiURL +'/' + imageURL.heatmap3categoryBar} alt="heatmap3"
              className={classes.featureHeatmapbar}/>
              <img src={Config.settings.apiURL +'/'+ imageURL.heatmap3category[0][topicID]} alt="heatmap3"
              className={classes.featureHeatmap}/>
              {heatmap3.map(item =>(
                <img
              src = {item}
              alt="heatmap3" 
              className={classes.featureHeatmap}
              />
              ))}

            </Grid>

      )  

        return (
            <div className={classes.jumbotron}>

                  {/* <Paper elevation={0} className={classes.card} > */}
                    <Typography variant="h5" paragraph={true} className={classes.card} >
                         Profiles
                    </Typography>
                    {/* </Paper> */}
                    <Paper elevation={4}>  
                        <Tabs
                          value={this.state.selectTab}
                          onChange={this.handleChange}
                          variant="scrollable"
                          scrollButtons="on"
                          textColor="primary" 
                          classes={{scrollable:classes.scroller, indicator:classes.myColor}}  
                        >
                          <Tab label="Binding Region Profiles" key={0} />
                          <Tab label="TSS Profiles" key={1} />
                          <Tab label="TES Profiles" key={2} />
                        </Tabs>
                        <Divider/>

                <Grid container direction="column" className={classes.mytags}>
                    {/* <CardContent className={classes.pcard}>  */}
                      <Grid item>
                        <Grid container direction="row" wrap="nowrap">        
                              <Grid item>                  
                                    <Grid container direction="column">
                                            <Grid item>
                                                  <Typography align = 'center' className={classes.tagpaper}>
                                                  Topic-{topicID}
                                                  </Typography>
                                            </Grid>
                                            <Grid item>
                                                  <img
                                                      src = {Config.settings.apiURL +'/'+ imageURL.averagePlot[0][topicID]}
                                                      alt="average" 
                                                      className={classes.featureHeatmap}
                                                    />
                                            </Grid>
                                      </Grid>
                                </Grid>

                              <Grid container direction="row" wrap="nowrap">

                                  {proteinList.map(item =>(
                                        <div>
                                            <Grid item>
                                                <Typography align = 'center' className={classes.tagpaper}>
                                                      {item}
                                                </Typography>
                                            </Grid>
                                            <Grid item>
                                                <img
                                                    src = {Config.settings.apiURL +'/'+ imageURL.averagePlot[0][item]}
                                                    alt="average" 
                                                    className={classes.featureHeatmap}
                                                  />
                                            </Grid>
                                        </div>

                                      ))}
                                </Grid>



                        </Grid>                  
                     </Grid>
                </Grid>
                      </Paper> 


            </div>      


    );
}}

Profiles.propTypes = {
    classes: PropTypes.object.isRequired,
  };

  export default withStyles(styles)(Profiles);
