import React from "react";
import axios from "axios";
import { withRouter} from 'react-router-dom';
import MUIDataTable from "mui-datatables";
import Typography from '@material-ui/core/Typography';
import LinearProgress from '@material-ui/core/LinearProgress';
import Config from '../Config';

class Datatable extends React.Component {
    handleRowClick = (rowData,rowMeta) => {
    //    console.log("row clicked \n" + rowData[1] );     
        this.props.updateContent(rowData[1])();          
    }

    state = {        
        data : [],
        loading : true,
        message: "Fetching Samples"
    }

    componentDidMount() {
        var dataURL = Config.settings.apiURL+Config.settings.samplesEndpoint;
        // var dataURL = "https://pluto.vmhost.psu.edu:8081/reviewSamples"
;       
        axios.get(dataURL)
        .then(res => {
                // console.log(res.data.count);
                console.log(res.data.samples);

                // retrieve the sample data
                let values = res.data.samples.map(sample =>{
                    return [sample.sampleId,sample.standardGeneName,sample.treatments,sample.growthMedia,sample.alias]
                });
                // let myJsonString = JSON.stringify(values);
                // console.log(myJsonString);
                this.setState({
                    data: values,
                    loading:false
                });
            }).catch(error =>{
                console.log(error);
                this.setState({
                    loading:true,
                    message: error.message + " / Server Offline"
                })                
            });  
    }

  render() {
    const columns = [ {
        name: "Sample ID",
        options: {
         filter: false,
         sort: true,         
        }
       }, 
       {
        name: "Common Name",
        options: {
         filter: false,
         sort: true,         
        }
        }, {
            name: "Condition",
            options: {
             filter: true,
             sort: true,         
            }
            },
            {
              name: "Growth Media",
              options: {
               filter: false,
               sort: true,         
              }
              },
        {
            name: "Alias",
            options: {
             filter: false,
             sort: true,
             display:false         
            }
            },

        ];

    const { data, loading, message } = this.state;

    const options = {
      filterType: "multiselect",
      responsive: "scroll",
      selectableRows: false,
      rowsPerPage: 10,
      rowsPerPageOptions:[5,10,15,20,50,100],
      onRowClick: this.handleRowClick,
      print:false,
      downloadOptions:{filename: 'sampleData.csv', separator: ','} 
    };



        // creating individuals posts
        const samplesTable = loading ? (  
            <Typography component="div" >                   
                <Typography component="p" variant="subtitle1" >
                    {message}
                </Typography>
                <LinearProgress variant="query" />
            </Typography> 

            ) : (
                <MUIDataTable
                    title={"Explore"}
                    data={data}
                    columns={columns}
                    options={options}        
                />
            );

        return (
            <div style={{maxWidth: 980, margin: '0 auto'}}>
             {samplesTable}
            </div>

        )
  }
}

export default withRouter(Datatable);
