import React from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import Typography from '@material-ui/core/Typography';
import CardContent from '@material-ui/core/CardContent';
import Divider from '@material-ui/core/Divider';
import AppBar from '@material-ui/core/AppBar';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Grid from '@material-ui/core/Grid';
import Paper from '@material-ui/core/Paper';

import comingup from '../comingup.png';

const styles = theme => ({
  jumbotron:{
    padding: '1rem 1rem',
    bottomMargin:'1rem',     
},
    center : {
        margin: 'auto',      
        minWidth: 1100,
        padding: 5,
        // border: '2px solid green'
      },
    card: {
        minWidth: 1100
    },         
    featureHeatmap:{
      width: 210,
      height:200,
      marginTop:35,
    //   border: '2px solid yellow'
  },  

      searchbar:{
        width: 1100
      },
      forbottom:{
          width: 280,     
        //   border: '2px solid yellow'
      },
      fortop:{
        width: 320,     
      //   border: '2px solid yellow'
    },
    leftSection: {
      // border: '2px solid gray'
    },
    rightSection:{
      // border: '2px solid blue'
    },
      mainContainer:{
          overflow: 'scroll'
      },
      colorBar:{
        height: 470,
        width: 20,
        marginTop:14,
        marginLeft: -15   
      },

});


class Profiles extends React.Component { 
  state = {
    selectTab :0,
    bindingRegionProfiles: this.props.topic.bindingRegionProfiles[0],
    tssProfiles: this.props.topic.tssProfiles[0],
    tesProfiles : this.props.topic.tesProfiles[0],
  }

  /*componentDidMount(){
    this.setState({
      selectTab :0,
      bindingRegionProfiles: this.props.topic.bindingRegionProfiles[0],
      tssProfiles: this.props.topic.tssProfiles[0],
      tesProfiles : this.props.topic.tesProfiles[0],
        //stringPicture: this.props.topic.stringPicture
});}*/

  componentWillReceiveProps(nextProps){
    this.setState({
      selectTab :0,
      bindingRegionProfiles: nextProps.topic.bindingRegionProfiles[0],
      tssProfiles: nextProps.topic.tssProfiles[0],
      tesProfiles : nextProps.topic.tesProfiles[0],
        //stringPicture: nextProps.topic.stringPicture
  });
  }

  handleChange = (event, selectedTab) => {

    selectedTab === 0 ? this.setState({
        selectedTab: selectedTab,
        bindingRegionProfiles: this.props.topic.bindingRegionProfiles[0],
    }) : 
    this.setState({
        selectedTab: selectedTab,
        tssProfiles: this.props.topic.tssProfiles[0],
        tesProfiles: this.props.topic.tesProfiles[0],
    })

  };

  render(){ 
    const {classes} = this.props;
    const{tabContent} = this.state;
    const bindingRegionProfiles = this.state.bindingRegionProfiles;
    const topicID = this.props.topic.topicID;
    const proteinList = this.props.topic.proteinList.split('\t');
    console.log(bindingRegionProfiles.averagePlot[0][proteinList[0]])
    console.log(proteinList[0])
    return (
            <div className={classes.jumbotron}>

                <Grid container justify = "center">
                  <Paper elevation={0} className={classes.card}>
                        <Typography variant="h5" paragraph={true}>
                                Profiles
                            </Typography>
                    <Paper>  
                        <Tabs
                          value={tabContent}
                          onChange={this.handleChange}
                          indicatorColor="primary"
                          textColor="primary"
                          variant="scrollable"
                          scrollButtons="on"            
                          classes={{scrollable:classes.scroller}}   
                        >
                          <Tab label="Binding Region Profiles" key={0} />
                          <Tab label="TSS Profiles" key={1} />
                          <Tab label="TES Profiles" key={2} />
                        </Tabs>
                        <Divider/>

                    <CardContent className={classes.sectionHolder}>   
                                  {/* Bottom Section */}          
                        <Typography component="div" >                       
                              <Grid container 
                                    direction="row"
                                    justify="space-evenly"
                                    alignItems="center"
                                    spacing={0}
                                    className={classes.mainContainer}
                                  >                       
                                  {<Grid item >
                                    <img src={"http://localhost:8080/" + bindingRegionProfiles.averagePlot[0][topicID]} alt="bindingRegionProfiles"
                                    className={classes.featureHeatmap}/>
                                  </Grid>}

                                  {<Grid item >
                                    <img src={"http://localhost:8080/" + bindingRegionProfiles.averagePlot[0][proteinList[0]]} alt="bindingRegionProfiles"
                                    className={classes.featureHeatmap}/>
                                  </Grid>}
                                  {<Grid item >
                                    <img src={"http://localhost:8080/" + bindingRegionProfiles.averagePlot[0][proteinList[1]]} alt="bindingRegionProfiles"
                                    className={classes.featureHeatmap}/>
                                  </Grid>}
                                  {<Grid item >
                                    <img src={"http://localhost:8080/" + bindingRegionProfiles.averagePlot[0][proteinList[2]]} alt="bindingRegionProfiles"
                                    className={classes.featureHeatmap}/>
                                  </Grid>}
                                  {<Grid item >
                                    <img src={"http://localhost:8080/" + bindingRegionProfiles.averagePlot[0][proteinList[3]]} alt="bindingRegionProfiles"
                                    className={classes.featureHeatmap}/>
                                  </Grid>}

                              </Grid>   
                        </Typography>          
                      </CardContent>
                      </Paper> 
                    </Paper> 


                </Grid>
            </div>      


    );
}}

Profiles.propTypes = {
    classes: PropTypes.object.isRequired,
  };

  export default withStyles(styles)(Profiles);
